import React from 'react'
import './SingleProductPage.css'
import Nav from '../Navigation/Nav'
import Products from '../Products/Products'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'


const SingleProductPage = ({match}) => {
  let ProductId = match.params.id
  let [Products, setProducts] = useState([])

  useEffect(() => {
    getProducts()
  }, [ProductId])


  let getProducts = async () => {
    let response = await fetch('http://localhost:8000/Products/Products/$(ProductId}/')
    let data = await response.json()
    setProducts(data)
  }


  return (

    <div className="SingleProductPage-Home">
      <Nav />
      {Products.map((Products, index) => (
      <div className="SingleProductPage-Container">
        <a href="#">
          {' '}
          <img className="SingleProductPage-Mainimg" src="#" />
        </a>
        <div className="SingleProductPage-Content">
          <span className="SingleProductPage-Title"></span>
          <span className="SingleProductPage-Price">$</span>
          <button className="SingleProductPage-button">ADD TO CART</button>
          <span className="SingleProductPage-desc"></span>
        </div>
    
      </div>
      ))}
    </div>
  )
}

export default SingleProductPage
